// src/pages/Portfolio.jsx
import React, { useState } from 'react';
import './Portfolio.css';

const Portfolio = () => {
  const [activeTab, setActiveTab] = useState('projects');

  const projects = [
    {
      id: 1,
      title: 'Aritmatika Solver',
      description: 'A sophisticated mathematical problem-solving application',
      image: '/project1.png',
    },
    {
      id: 2,
      title: 'Auto-Chat-Discord',
      description: 'AI Bot For Your Discord Server',
      image: '/project2.png',
    },
    // Add more projects as needed
  ];

  const certificates = [
    {
      id: 1,
      title: 'Web Development',
      issuer: 'Coursera',
      date: '2023',
    },
    {
      id: 2,
      title: 'React Development',
      issuer: 'Udemy',
      date: '2023',
    },
    {
      id: 3,
      title: 'Advanced JavaScript',
      issuer: 'Udacity',
      date: '2023',
    },
  ];

  const skills = [
    { id: 1, name: 'ReactJS', icon: '/react-icon.png' },
    { id: 2, name: 'Node.js', icon: '/node-icon.png' },
    { id: 3, name: 'Firebase', icon: '/firebase-icon.png' },
    { id: 4, name: 'HTML5', icon: '/html-icon.png' },
    { id: 5, name: 'CSS3', icon: '/css-icon.png' },
    { id: 6, name: 'JavaScript', icon: '/js-icon.png' },
  ];

  return (
    <div className="portfolio-container">
      <h1>Portfolio Showcase</h1>
      <p className="subtitle">
        Explore My Journey Through Projects, Certificates, And Technical Expertise. Each Section
        Represents a Milestone InMy Continuous Learning Path
      </p>

      <div className="tabs">
        <button
          className={`tab ${activeTab === 'projects' ? 'active' : ''}`}
          onClick={() => setActiveTab('projects')}
        >
          Projects
        </button>
        <button
          className={`tab ${activeTab === 'certificates' ? 'active' : ''}`}
          onClick={() => setActiveTab('certificates')}
        >
          Certificates
        </button>
        <button
          className={`tab ${activeTab === 'skills' ? 'active' : ''}`}
          onClick={() => setActiveTab('skills')}
        >
          My Skills
        </button>
      </div>

      <div className="content">
        {activeTab === 'projects' && (
          <div className="projects-grid">
            {projects.map((project) => (
              <div key={project.id} className="project-card">
                <div className="project-image">
                  <img src={project.image} alt={project.title} />
                </div>
                <h3>{project.title}</h3>
                <p>{project.description}</p>
              </div>
            ))}
          </div>
        )}

        {activeTab === 'certificates' && (
          <div className="certificates-grid">
            {certificates.map((cert) => (
              <div key={cert.id} className="certificate-card">
                <h3>{cert.title}</h3>
                <p>{cert.issuer}</p>
                <p>{cert.date}</p>
              </div>
            ))}
          </div>
        )}

        {activeTab === 'skills' && (
          <div className="skills-grid">
            {skills.map((skill) => (
              <div key={skill.id} className="skill-item">
                <img src={skill.icon} alt={skill.name} />
                <p>{skill.name}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Portfolio;
