// src/pages/Portfolio.jsx
import React, { useState } from 'react';
import { FaCode, FaCertificate, FaTools } from 'react-icons/fa';

const Portfolio = () => {
  const [activeTab, setActiveTab] = useState('projects');

  const projects = [
    {
      title: 'Aritmatika Solver',
      description: 'A powerful math engine for solving expressions, simplifying equations, and visualizing graphs.',
      image: 'https://via.placeholder.com/400x240?text=Aritmatika+Solver',
    },
    {
      title: 'Auto-Chat-Discord',
      description: 'AI Bot for your Discord server with smart commands and moderation.',
      image: 'https://via.placeholder.com/400x240?text=Discord+Bot',
    },
  ];

  const certificates = [
    { title: 'AWS Certified', description: 'Cloud fluency and foundational AWS knowledge.' },
    { title: 'Google ML Crash Course', description: 'Intro to machine learning using Google tools.' },
  ];

  const skills = ['React.js', 'Node.js', 'Tailwind CSS', 'MongoDB', 'Machine Learning'];

  return (
    <section id="portfolio" className="bg-[#0a0014] text-white py-16 px-6 md:px-20">
      <h2 className="text-4xl font-bold text-center mb-2">
        <span className="text-white">Portfolio </span>
        <span className="text-pink-500">Showcase</span>
      </h2>
      <p className="text-center text-gray-400 mb-8 max-w-2xl mx-auto">
        Explore My Journey through Projects, Certificates, and Technical Expertise. Each Section Represents a Milestone in My Continuous Learning Path.
      </p>

      {/* Buttons */}
      <div className="flex flex-wrap justify-center gap-4 mb-12">
        <button
          onClick={() => setActiveTab('projects')}
          className={`px-5 py-2 rounded-full font-semibold transition ${
            activeTab === 'projects' ? 'bg-pink-500 text-white' : 'border border-white'
          }`}
        >
          <FaCode className="inline mr-2" /> Projects
        </button>
        <button
          onClick={() => setActiveTab('certificates')}
          className={`px-5 py-2 rounded-full font-semibold transition ${
            activeTab === 'certificates' ? 'bg-pink-500 text-white' : 'border border-white'
          }`}
        >
          <FaCertificate className="inline mr-2" /> Certificate
        </button>
        <button
          onClick={() => setActiveTab('skills')}
          className={`px-5 py-2 rounded-full font-semibold transition ${
            activeTab === 'skills' ? 'bg-pink-500 text-white' : 'border border-white'
          }`}
        >
          <FaTools className="inline mr-2" /> My Skill
        </button>
      </div>

      {/* Content */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
        {activeTab === 'projects' &&
          projects.map((proj, i) => (
            <div key={i} className="bg-[#1c1b2e] p-5 rounded-lg shadow-md hover:scale-105 transition">
              <img src={proj.image} alt={proj.title} className="rounded mb-4 w-full h-52 object-cover" />
              <h3 className="text-xl font-bold mb-2">{proj.title}</h3>
              <p className="text-gray-300">{proj.description}</p>
            </div>
          ))}

        {activeTab === 'certificates' &&
          certificates.map((cert, i) => (
            <div key={i} className="bg-[#1c1b2e] p-5 rounded-lg shadow-md">
              <h3 className="text-xl font-bold mb-2">{cert.title}</h3>
              <p className="text-gray-300">{cert.description}</p>
            </div>
          ))}

        {activeTab === 'skills' && (
          <div className="col-span-full text-center">
            <ul className="flex flex-wrap justify-center gap-4 mt-4">
              {skills.map((skill, i) => (
                <li key={i} className="bg-pink-600 text-white px-4 py-2 rounded-full shadow-sm">
                  {skill}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </section>
  );
};

export default Portfolio;
